import React, { Component } from "react";

import firebase, {
  provider,
  auth
} from "../firebase.js";

class ShoppingCart extends Component {
  constructor(props) {
    super();
    this.state = {
      items: [],
      total: null
    };
  }

  componentDidMount() {
    const itemsRef = firebase
      .database()
      .ref("items");

    console.log("ItemsRef: ", itemsRef);

    itemsRef.on(
      "value",
      snapshot => {
        let items1 = snapshot.val();
        console.log("A", items1);
        const itemsArray =
          items1 && Object.entries(items1);

        let newState = [];

        itemsArray &&
          itemsArray.map(item => {
            newState.push({
              id: item[0],
              title: item[1].title,
              user: item[1].user
            });
          });
        console.log("B", newState);

        this.setState({
          items: newState
        });
      },
      error => {
        console.error("Error: ", error);
      }
    );
  }

  render() {
    return (
      <div>
        <h2>Your Basket</h2>
        map
        <section>
          {this.state.items.map(item => {
            return (
              <div key={item.id}>
                <ul>
                  <li>
                    <h3>{item.title}</h3>
                    <p>User: {item.user}</p>
                  </li>
                </ul>
              </div>
            );
          })}
        </section>

      </div>
    );
  }
}
export default ShoppingCart;
